import Emoji from '../base/Emoji';
import Text from '../base/Text';

const StyledCard = styled.div`
    background-color: white;
    display: inline-block;
    padding: 20px;
    position: relative;
    border-radius: 15px;
    box-shadow: 2px 2px 10px rgba(0,0,0,0.3);
`


